import { useEffect, useState } from "react";


const tempWatchedData = [
  {
    imdbID: "tt1375666",
    Title: "Inception",
    Year: "2010",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg",
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: "tt0088763",
    Title: "Back to the Future",
    Year: "1985",
    Poster:
      "https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);
const key = 'd66e8508';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedID] = useState(null);

  function handleSelectMovie(ID){
    setSelectedID(ID);
  }

  useEffect(function() { 
  console.log('A')
    })

  useEffect(function() { 
    console.log('it is me all the the problem is me')
    },[query])

  console.log('C')
 useEffect(function(){
  async function fetchMovie() {
    try {
      setLoading(true);
      setError("");
      const res = await fetch(
        `http://www.omdbapi.com/?apikey=${key}&s=${query}`
      );
      if (!res.ok) throw new Error("Response Error: " + res.text());

      const data = await res.json();
      if (data.Response === "False") throw new 
      Error("Movie not found");

      setMovies(data.Search);
      console.log(data.Search)
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }
  if (query.length < 3){
    setMovies([]);
    setError("");
    return;
  }
  fetchMovie();
 },[query])


  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery}/>
        <NumResults movies={movies} />
      </NavBar>

      <Main>
      <Box>
      {/* {isLoading ? <Loader /> : <MovieList movies={movies} />} */}
      {isLoading && <Loader />}
          {!isLoading && !error && (
            <MovieList movies={movies}/>
          )}
          {error && <ErrorMessage message={error} />}
      </Box>

        <Box>   
         { 
          onSelectMovie ? <MovieDetails onSelectMovie={handleSelectMovie} /> : (
            <>
             <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
            </>
          )
        }
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function MovieDetails({selectedID}) {
  return <div className="details">{selectedID}</div>;
}

function ErrorMessage({message}){
  return (
    <p className="error">
    <span >üö®</span> {message}
    </p>
  )
}


function Loader(){
  return (
   <p className="loader">loading...</p>
      )
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">üçø</span>
      <h1>usePopcorn</h1>
    </div>
  );
}

function Search({query, setQuery}) { 

  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "‚Äì" : "+"}
      </button>

      {isOpen && children}
    </div>
  );
}

