import { useEffect, useState } from "react";
import StartRating from "./StartRating";


const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);
const key = 'd66e8508';

export default function App() {
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedID] = useState(null);

  function handleSelectMovie(ID){
    setSelectedID(selectedId=> ID === selectedId ? null : ID);
  }
  function handleCloseMovie()
  {
    setSelectedID(null);
  }
  useEffect(function() { 
  console.log('A')
    })

  useEffect(function() { 
    console.log('it is me all the the problem is me')
    },[query])

  console.log('C')
 useEffect(function(){
  async function fetchMovie() {
    try {
      setLoading(true);
      setError("");
      const res = await fetch(
        `http://www.omdbapi.com/?apikey=${key}&s=${query}`
      );
      if (!res.ok) throw new Error("Response Error: " + res.text());

      const data = await res.json();
      if (data.Response === "False") throw new 
      Error("Movie not found");

      setMovies(data.Search);
      console.log(data.Search)
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }
  if (query.length < 3){
    setMovies([]);
    setError("");
    return;
  }
  fetchMovie();
 },[query])


  return (
    <>
      <NavBar>
        <Search query={query} setQuery={setQuery}/>
        <NumResults movies={movies} />
      </NavBar>

      <Main>
      <Box>
      {/* {isLoading ? <Loader /> : <MovieList movies={movies} />} */}
      {isLoading && <Loader />}
          {!isLoading && !error && (
            <MovieList movies={movies} onSelectMovie={handleSelectMovie} />
          )}
          {error && <ErrorMessage message={error} />}
      </Box>

        <Box>   
         { 
          selectedId ? <MovieDetails selectedID={selectedId} onCloseMovie={handleCloseMovie}/> : (
            <>
             <WatchedSummary watched={watched} />
          <WatchedMoviesList watched={watched} />
            </>
          )
        }
        </Box>
      </Main>
    </>
  );
}

function NavBar({ children }) {
  return (
    <nav className="nav-bar">
      <Logo />
      {children}
    </nav>
  );
}

function MovieDetails({selectedID, onCloseMovie}) {
  const [movie, setMovie] = useState({}); 
  const {
    Title: title,
    Year: year,
    Poster: poster,
    Runtime: runtime,
    imdbRating,
    Plot: plot,
    Released: released,
    Actors: actors,
    Director: director,
    Genre: genre,
  } = movie;
  console.log(title, year, poster, runtime, imdbRating, plot, released, actors, director, genre);
  useEffect(function(){
    async function getMovieDetails(){
      const res = await fetch(
        `http://www.omdbapi.com/?apikey=${key}&i=${selectedID}`
      );
      const data = await res.json();
      console.log(data);

    }
    getMovieDetails();
  },[]);


  return (<div className="details">
         <header>
            <button className="btn-back" onClick={onCloseMovie}>
              &larr;
            </button>
            <img src={data.poster} alt={`Poster of ${movie} movie`} />
          </header>
          {selectedID}
          </div>)
}

function ErrorMessage({message}){
  return (
    <p className="error">
    <span >🚨</span> {message}
    </p>
  )
}


function Loader(){
  return (
   <p className="loader">loading...</p>
      )
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🍿</span>
      <h1>usePopcorn</h1>
    </div>
  );
}

function Search({query, setQuery}) { 

  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function NumResults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
    </p>
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "–" : "+"}
      </button>

      {isOpen && children}
    </div>
  );
}



function MovieList({ movies , onSelectMovie}) {
  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} onSelectMovie={onSelectMovie}/>
      ))}
    </ul>
  );
}

function Movie({ movie , onSelectMovie }) {
  return (
    <li onClick={() => onSelectMovie(movie.imdbID)}>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  );
}

function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));

  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgImdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}

function WatchedMoviesList({ watched }) {
  return (
    <ul className="list">
      {watched.map((movie) => (
        <WatchedMovie movie={movie} key={movie.imdbID} />
      ))}
    </ul>
  );
}

function WatchedMovie({ movie }) {
  return (
    <li>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>⭐️</span>
          <span>{movie.imdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{movie.userRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{movie.runtime} min</span>
        </p>
      </div>
    </li>
  );
}


